$(window).on("load", function() {
    var wrap = ".about-content .page-content-block";
    var content = ".about-content .page-content-wrap";
    var aboutPagePercentage = 0;
    var aboutScrollInterval = new Object;

    function aboutScrollCalculate() {
        if (getViewState.name() != "about") {
            clearInterval(aboutScrollInterval)
        } else {
            aboutScrollInterval = setInterval(function() {
                var wintop = $(wrap).scrollTop(),
                    docheight = $(content).height(),
                    winheight = $(window).height();
                var totalScroll = wintop / (docheight - winheight) * 100;
                $(".about-progress-bar .track .track-elem").css("width", totalScroll + "%");
                aboutPagePercentage = totalScroll
            }, 100)
        }
    }
    aboutScrollCalculate();
    $(window).on("hashchange", function() {
        aboutScrollCalculate()
    })
});
